def get_star_display(score: float) -> str:
    """å°†åˆ†æ•°è½¬æ¢ä¸ºæ˜Ÿæ˜Ÿæ˜¾ç¤ºï¼ˆæ”¯æŒ0.25/0.5/0.75ï¼‰"""
    full_stars = int(score)
    remainder = score - full_stars
    
    stars = "â˜…" * full_stars
    
    if remainder >= 0.75:
        stars += "â­"  # 0.75æ˜Ÿ
    elif remainder >= 0.5:
        stars += "â˜†"  # 0.5æ˜Ÿ
    elif remainder >= 0.25:
        stars += "âœ¦"  # 0.25æ˜Ÿ
    
    empty_stars = 5 - full_stars - (1 if remainder > 0 else 0)
    stars += "â˜†" * empty_stars
    
    return stars


